INSERT INTO tb_position
    (name, description)
VALUES
    ('ATACANTE', 'Encarregado de concluir jogadas, seja realizando passes decisivos ou marcando gols para a equipe.'),
    ('CENTROAVANTE', 'Jogador que atua próximo à área adversária, responsável por finalizar jogadas com gols, sendo a referência principal do ataque.'),
    ('GOLEIRO', 'Defensor do gol, responsável por proteger a meta e realizar defesas fundamentais para a equipe.'),
    ('LATERAL DIREITO', 'Contribui para a defesa e apoia o ataque, fornecendo assistência aos atacantes com avanços pelo lado direito do campo.'),
    ('LATERAL ESQUERDO', 'Desempenha papel defensivo e ofensivo, ajudando na defesa e prestando assistência aos atacantes pelo lado esquerdo do campo.'),
    ('MEIA ATACANTE', 'Atua tanto na criação quanto na finalização das jogadas, sendo uma peça-chave na articulação do jogo.'),
    ('MEIA DIREITO', 'Focado na criação de jogadas no meio do campo, buscando oportunidades de ataque pelo lado direito.'),
    ('MEIA ESQUERDO', 'Similar ao meia direito, concentra-se na criação de jogadas, explorando oportunidades de ataque pelo lado esquerdo.'),
    ('PRIMEIRO VOLANTE', 'Encarregado da marcação e da transição defesa-ataque, desempenhando um papel vital na distribuição do jogo.'),
    ('PONTA DIREIRA', 'Avança pelas laterais direitas, cruzando bolas na área e contribuindo com o ataque do time.'),
    ('PONTA ESQUERDA', 'Similar à ponta direita, avança pelas laterais esquerdas, cruzando bolas na área e participando do jogo ofensivo.'),
    ('SEGUNDO VOLANTE', 'Auxilia o primeiro volante, equilibrando o meio de campo e contribuindo tanto na defesa quanto no ataque.'),
    ('TERCEIRO ZAGUEIRO', 'Zagueiro que atua como líbero, oferecendo suporte adicional à defesa em momentos estratégicos.'),
    ('ZAGUEIRO CANHOTO', 'Similar ao zagueiro destro, responsável pela defesa e também podendo surpreender estrategicamente.'),
    ('ZAGUEIRO DESTRO', 'Encarregado da marcação sólida e da proteção do gol, ocasionalmente atuando como uma surpresa tática.');
